import { Component, useState } from 'react';
import { ContactForm } from './ContactForm/ContactForm';
import { ContactsList } from './ContactsList/ContactsList';
import { Filter } from './Filter/Filter';
import css from '../components/App.module.css'
export const App = () => {
  state = {
    contacts: [
      { id: 'id-1', name: 'Rosie Simpson', number: '459-12-56' },
      { id: 'id-2', name: 'Hermione Kline', number: '443-89-12' },
      { id: 'id-3', name: 'Eden Clements', number: '645-17-79' },
      { id: 'id-4', name: 'Annie Copeland', number: '227-91-26' },
    ],
    filter: '',
  };
  const[contacts, useContacts] = useState();
  const[filter, useFilter] = useState();


  componentDidMount(); {
    const savedContacts = localStorage.getItem('contacts')
    if (savedContacts) {
      this.setState({contacts:JSON.parse(savedContacts)})
    }
  };
  componentDidUpdate(_, prevState); {
    if (this.state.contacts.length !== prevState.contacts.length) {
      localStorage.setItem('contacts', JSON.stringify(this.state.contacts))
    }
  };

  handleAddContact = userContacts => {
    if (
      this.state.contacts.some(
        contact =>
          contact.name.toLowerCase() === userContacts.name.toLowerCase()
      )
    ) {
      alert(`${userContacts.name} is already in contacts`);
      return;
    }
    this.setState({
      contacts: [userContacts, ...this.state.contacts],
    });
  };

  handleFilterChange = e => {
    this.setState({
      filter: e.target.value,
    });
  };
  getContactFromFilter = () => {
    const { contacts, filter } = this.state;
    const filterContacts = contacts.filter(({ name }) =>
      name.toLowerCase().includes(filter.toLowerCase())
    );
    return filterContacts;
  };
  handleDelete = contactId => {
    this.setState(prevState => {
      return {
        contacts: prevState.contacts.filter(
          contact => contact.id !== contactId
        ),
      };
    });
  };

    return (
      <div className={css.container}>
        <h1>Phonebook</h1>
        <ContactForm handleAddContact={this.handleAddContact} />
        <h2>Contacts</h2>
        <Filter
          filter={this.filter}
          handleFilterChange={this.handleFilterChange}
        />
        <ContactsList
          contacts={this.getContactFromFilter()}
          handleDelete={this.handleDelete}
        />
      </div>
    );

}
